<?phpadd_action( 'woocommerce_product_options_general_product_data', 'woo_add_custom_general_fields' );add_action( 'woocommerce_process_product_meta', 'woo_add_custom_general_fields_save' );function woo_add_custom_general_fields() {    global $woocommerce, $post;    echo '<div class="options_group">';    woocommerce_wp_text_input(        array(            'id'          => 'baohanh',            'label'       => __( 'Bảo hành', 'woocommerce' ),            'desc_tip'    => 'true',            'description' => __( 'Bảo hành', 'woocommerce' )        )    );    echo '</div>';}function woo_add_custom_general_fields_save( $post_id ){    $woocommerce_text_field = $_POST['baohanh'];    if( !empty( $woocommerce_text_field ) )        update_post_meta( $post_id, 'baohanh', esc_attr( $woocommerce_text_field ) );}